INSERT INTO `xoousers_email_templates` (`template_id`, `template_name`, `template_text`) VALUES
(1, 'Account Confirmation', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Thank your for signing up. </p>\r\n\r\n<b>Please CONFIRM YOUR ACCOUNT.</b>\r\n<p>Account Confirmation Instructions:</p>\r\n<p>Pelase click on the link below to confirm your account.</p>\r\n\r\n<p><a href="{{confirmation_link}}">CLICK HERE TO CONFIRM YOUR ACCOUNT</a></p>\r\n<p>Link does not work? Copy the following link to your browser address bar:</p>\r\n\r\n<p>{{confirmation_link}}</p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>'),
(2, 'New Password', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Your password has been reset. </p>\r\n\r\n<h2>Login information:</h2>\r\n<p>User name: <b>{{user_nick}}</b></p>\r\n<p>Password: <b>{{user_password}}</b></p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>'),
(3, 'Account Confirmation Sucess', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Your account has been confirmed. </p>\r\n\r\n<h2>Login information:</h2>\r\n<p>User name: <b>{{user_nick}}</b></p>\r\n<p>Password: <b>(same used when signed up)</b></p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>'),
(4, 'Account Confirmation Numeric', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Thank you for signing up. </p>\r\n\r\n<b>Please CONFIRM YOUR ACCOUNT.</b>\r\n<h2>Account Confirmation Instructions:</h2>\r\n\r\n<p>Confirmation Number: <b>{{confirmation_number}}</b></p>\r\n<p>Pelase click on the link below to confirm your account.</p>\r\n\r\n<p><a href="{{confirmation_link}}">CLICK HERE TO CONFIRM YOUR ACCOUNT</a></p>\r\n<p>Link does not work? Copy the following link to your browser address bar:</p>\r\n\r\n<p>{{confirmation_link}}</p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>\r\n'),
(5, 'Account Confirmation Manual', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Thank your for signing up. </p>\r\n\r\n<b>We will activate your account within 48 hours.</b>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>'),
(6, 'Account Confirmation Facebook, Google & Yahoo', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Thank you for signing up. </p>\r\n<p>Your account has been confirmed and is ready to use. </p>\r\n\r\n<h2>Login information:</h2>\r\n<p>User name: <b>{{user_nick}}</b></p>\r\n<p>Password: <b>{{user_password}}</b></p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>'),
(7, 'Password Reset Link', '<h1>Hello {{user_name}},</h1>\r\n\r\n<p>Please use the link below so reset your password. </p>\r\n\r\n<p><a href="{{reset_link}}">{{reset_link}}</a></p>\r\n\r\n<p>Please let us know if you have any questions.,<br />\r\nSupport Department.<br />\r\n{{company_name}}<br />\r\n{{company_email}}<br />\r\n\r\n</p>');
